import React, { useState,useRef } from 'react';
import axios from 'axios';
//import { useHistory } from 'react-router-dom';

const InputShortener = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const errRef = useRef();
  const [errMsg, setErrMsg] = useState(null);


  if (originalUrl === '') {
    setError('Please enter a value.');
    setIsSubmitting(false);
    return;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
    await axios.post('http://localhost:9618/api/generate',  {originalUrl} )
    .then((response)=>{
      console.log(response)
    })
    setOriginalUrl('');

  }catch(err){
    if (!err?.response) {
      setErrMsg('No Server Response');
  } else if (err.response?.status === 204) {
      setErrMsg('Invalid Url');
  } else if (err.response?.status === 404) {
    setErrMsg('Url does not exist!!');
} else if (err.response?.status === 504) {
  setErrMsg('Url Expired. Please try generating a fresh one.');
} else {
  setErrMsg('Something went wrong')
}
  errRef.current.focus();

  }
}
  return (
    <div className="inputContainer">
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <h1>URL <span>Shortener</span></h1>
      <form onSubmit={handleSubmit}>
      <input type="text" value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} />
      <button type="submit">Submit</button>
    </form>
    </div>
  )
}

export default InputShortener
