import React, { useState, useRef } from 'react';
import axios from 'axios';
//import { useHistory } from 'react-router-dom';

const InputShortener = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [shortlUrl, setShortUrl] = useState('');
  const [redirectUrl, setRedirectUrl] = useState('');
  const errRef = useRef();
  const [errMsg, setErrMsg] = useState(null);

  const getData = (shortlUrl) => {
    fetch(`http://localhost:9618/api/${shortlUrl}`)
    .then((response) => {
      console.log(response.data.shortLink);

})

  }
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (originalUrl.length < 12) {
        setErrMsg("Please Enter valid Url")
        return false;
      }

      await axios.post('http://localhost:9618/api/generate', { originalUrl })
        .then((response) => {
          console.log(response.data.shortLink);
          setShortUrl(response.data.shortLink);

        })
      setOriginalUrl('');

    } catch (err) {
      if (!err?.response) {
        setErrMsg('No Server Response');
        return false;
      } else if (err.response?.status === 204) {
        setErrMsg('Invalid Url');
        return false;
      } else {
        setErrMsg('URL Not Genrated ')
        return false;
      }
      errRef.current.focus();

    }
  }
  return (
    <div className="inputContainer">
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <h1>URL <span>Shortener</span></h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} />
        <button type="submit">Submit</button>
      </form>

      {/* <h4><a href={"http://localhost:9618/api/"+shortlUrl}> Short Url</a></h4> */}
      <button onSubmit={getData()}>Get Short URL</button>

    </div>
  )
}

export default InputShortener
