import React, { useState } from 'react';
import axios from 'axios';
//import { useHistory } from 'react-router-dom';

const InputShortener = () => {
  const [formData, setFormData] = useState({
    shortLink: ''
  });


  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:9618/api/generate', {
      shortLink: formData.shortLink

    })
      .then(response => {
        console.log('Response:', response);
        // history.push('/success', { response: response.data });
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };

  const handleInputChange = (event) => {
    const { shortLink, value } = event.target;
    setFormData({ ...formData, [shortLink]: value });
  };


  return (
    <div className="inputContainer">
      <h1>URL <span>Shortener</span></h1>
      <div>
        <input type="text" name="shortLink" placeholder="Paste a link to shorten it" value={formData.shortLink} onChange={handleInputChange}
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  )
}

export default InputShortener
