import React, { useState, useRef } from 'react';
import axios from 'axios';
//import { useHistory } from 'react-router-dom';

const InputShortener = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [shortlUrl, setShortUrl] = useState('');
  const [redirectUrl, setRedirectUrl] = useState('');
  const errRef = useRef();
  const [errMsg, setErrMsg] = useState(null);
  const URL = 'http://localhost:9618/api/'

//   const getData = () => {
//     setRedirectUrl(URL+shortlUrl)
// }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (originalUrl.length < 16) {
        setErrMsg("Please Enter valid Url")
        return false;
      }

      await axios.post(URL+'generate', { originalUrl })
        .then((response) => {
          console.log(response.data.shortLink);
          setShortUrl(response.data.shortLink);
        })
        setRedirectUrl(URL+shortlUrl)
        setOriginalUrl('');

    } catch (err) {
      if (!err?.response) {
        setErrMsg('No Server Response');
        return false;
      } else if (err.response?.status === 204) {
        setErrMsg('Invalid Url');
        return false;
      } else {
        setErrMsg('URL Not Genrated ')
        return false;
      }
      errRef.current.focus();

    }
  }
  return (
    <div className="inputContainer">
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <h1>URL <span>Shortener</span></h1>
      <form onSubmit={handleSubmit}>
        <input type="url" value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} />
        <button type="submit">Submit</button>
      </form>
      {/* <div>Your Short URL: http://localhost:9618/api/{shortlUrl} </div> */}
      {/* <h4><a href={redirectUrl}> Redirect To Orignal URL</a></h4> */}
      <div>{redirectUrl}</div>


    </div>
  )
}

export default InputShortener
